-- ----------------------------
--  Table structure for `cms_ad`
-- ----------------------------
DROP TABLE IF EXISTS `cms_ad`;
CREATE TABLE `cms_ad` (
  `ad_id` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `position_id` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '0,站外广告；从1开始代表的是该广告所处的广告位，同表ad_position中的字段position_id的值',
  `media_type` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '广告类型，0，图片；1，flash;2, 代码；3，文字',
  `ad_name` varchar(60) NOT NULL COMMENT '广告名称',
  `ad_link` varchar(255) NOT NULL COMMENT '广告链接地址',
  `ad_code` text NOT NULL COMMENT '广告链接的表现，文字广告就是文字或图片和flash就是它们的地址，代码广告就是代码内容',
  `start_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '广告开始时间',
  `end_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '广告结束时间',
  `enabled` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '广告是否关闭，1，开启；0，关闭；关闭后广告将不再有效，直至重新开启',
  `order` mediumint(8) unsigned NOT NULL DEFAULT '50' COMMENT '排序',
  PRIMARY KEY (`ad_id`),
  KEY `position_id` (`position_id`),
  KEY `enabled` (`enabled`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COMMENT='广告列表';

