DROP TABLE IF EXISTS `cms_article`;
CREATE TABLE `cms_article` (
  `article_id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增ID',
  `cat_id` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '文章的分类',
  `model` varchar(50) NOT NULL COMMENT '模型',
  `title` varchar(150) NOT NULL DEFAULT '' COMMENT '文章题目',
  `author` varchar(30) NOT NULL DEFAULT '' COMMENT '文章作者',
  `keywords` varchar(255) NOT NULL DEFAULT '' COMMENT '文章的关键字',
  `is_hot` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否热门',
  `article_type` tinyint(1) unsigned NOT NULL DEFAULT '2' COMMENT '0，普通；1，置顶；',
  `add_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '文章添加时间',
  `description` varchar(255) NOT NULL DEFAULT '' COMMENT '文章描述',
  `views` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '浏览数',
  `cover` varchar(100) NOT NULL COMMENT '封面',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '显示状态',
  PRIMARY KEY (`article_id`),
  KEY `cat_id` (`cat_id`)
) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=utf8 COMMENT='文章内容表';

