-- ----------------------------
DROP TABLE IF EXISTS `cms_attribute`;
CREATE TABLE `cms_attribute` (
  `attr_id` smallint(5) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增ID号',
  `cat_id` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '商品类型，同goods_type的cat_id',
  `attr_name` varchar(60) NOT NULL COMMENT '属性名称',
  `attr_input_type` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '当添加商品时，该属性的添加类别；0，为手工输入；1，为选择输入；2，为多行文本输入',
  `attr_type` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '属性是否多选；0，否；1，是；如果可以多选，则可以自定义属性，并且可以根据值的不同定不同的价',
  `attr_values` text NOT NULL COMMENT '如果attr_input_type为1，即选择输入，则attr_name对应的值的取值就是该字段的值',
  `sort_order` tinyint(3) unsigned NOT NULL DEFAULT '50' COMMENT '属性显示的顺序',
  `attr_group` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '属性分组，相同的为一个属性组。该值应该取自goods_type的attr_group的值的顺序',
  PRIMARY KEY (`attr_id`),
  KEY `cat_id` (`cat_id`)
) ENGINE=MyISAM AUTO_INCREMENT=212 DEFAULT CHARSET=utf8 COMMENT='商品类型属性表';

