  KEY `shipping_status` (`shipping_status`) COMMENT '(null)',
  KEY `pay_status` (`pay_status`) COMMENT '(null)',
  KEY `pay_id` (`pay_id`) COMMENT '(null)'
) ENGINE=MyISAM AUTO_INCREMENT=22 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='订单表';

